// HomePage.js
import React from 'react';
import Navbar from './Navbar'; // Importa el componente Navbar
import Button from './Button'; // Reutiliza el componente Button
import './HomePage.css'; // Asegúrate de crear este archivo CSS

const HomePage = () => {
  // Define los elementos del menú para pasarlos al Navbar
  const menuItems = [
    { label: 'Inicio', path: '/' },
    { label: 'Productos', path: '/productos' },
    { label: 'Cuenta', path: '/cuenta' },
    { label: 'Acerca de', path: '/acerca-de' },
  ];

  return (
    <div className="home-page">
      <header className="home-page__header">
        {/* Asegúrate de tener una imagen llamada logo.png en tu carpeta de assets */}
        <img src="logo.png" alt="Logo" className="home-page__logo" />
        <Navbar menuItems={menuItems} /> {/* Pasa los elementos del menú al Navbar */}
      </header>
      <section className="home-page__banner">
        <h2 className="home-page__title">Tecnología al alcance de tu mano</h2>
        <p className="home-page__description">
          Descubre la última palabra en innovación en Tecno Store, tu destino definitivo para los gadgets más modernos y la tecnología de punta. Desde smartphones hasta sistemas de sonido, nuestra selección exclusiva está diseñada para enriquecer tu vida diaria y llevarte al siguiente nivel de la experiencia digital. Con Tecno Store, estás a un clic de lo extraordinario.
        </p>
        <Button>Descubre nuestros productos</Button>
      </section>
      <div className="home-page__images">
        {/* Asumiendo que tienes imágenes llamadas product1.png, product2.png, product3.png */}
        <img src="product1.png" alt="Producto 1" className="home-page__product-image" />
        <img src="product2.png" alt="Producto 2" className="home-page__product-image" />
        <img src="product3.png" alt="Producto 3" className="home-page__product-image" />
      </div>
    </div>
  );
};

export default HomePage;
