// Login.js
import React, { useState } from 'react';
import InputField from './InputField';
import Button from './Button';
import logo from './TECNO.png';
import eyeClosed from './eye_closed.png';
import eyeOpen from './eye_icon.png';
import './Login.css';
import Register from './Register';
const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const togglePasswordVisibility = () => {
    setIsPasswordVisible(!isPasswordVisible);
  };

  return (
    <div className="login">
      <div className="login__logo-container">
        <img src={logo} alt="Logo Tecno Store" className="login__logo" />
      </div>
      <h1 className="login__title">Iniciar Sesión</h1>
      <InputField
        type="email"
        placeholder="Correo electrónico"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <InputField
        type={isPasswordVisible ? 'text' : 'password'}
        placeholder="Contraseña"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        icon={isPasswordVisible ? eyeOpen : eyeClosed}
        onIconClick={togglePasswordVisibility}
      />
      <Button>Iniciar Sesión</Button>
      <p className="login__register">No tienes una cuenta? <a href="Register.js" className="login__register-link">Regístrate</a></p>
    </div>
  );
};

export default Login;
