// Register.js
import React, { useState } from 'react';
import InputField from './InputField';
import Button from './Button';
import logo from './TECNO.png';
import eyeClosed from './eye_closed.png';
import eyeOpen from './eye_icon.png';
import './Login.css';

const Register = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const togglePasswordVisibility = () => {
    setIsPasswordVisible(!isPasswordVisible);
  };

  return (
    <div className="register">
      <div className="login__logo-container">
        <img src={logo} alt="Logo Tecno Store" className="login__logo" />
      </div>
      <h1 className="login__title">Regístrate</h1>
      <InputField
        type="text"
        placeholder="Nombre de usuario"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <InputField
        type="email"
        placeholder="Correo electrónico"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <InputField
        type={isPasswordVisible ? 'text' : 'password'}
        placeholder="Contraseña"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        icon={isPasswordVisible ? eyeOpen : eyeClosed}
        onIconClick={togglePasswordVisibility}
      />
      <Button>Registrarse</Button>
      <p className="login__login">Ya tienes una cuenta? <a href="/login" className="login__login-link">Iniciar Sesión</a></p>
    </div>
  );
};

export default Register;
