// Login.jsx
import React, { useState } from 'react';
import InputField from './InputField';
import LoginButton from './LoginButton';
import './Login.css'; // Este es el archivo CSS que ya tienes

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = () => {
    // Lógica de inicio de sesión
    console.log(email, password);
  };

  return (
    <div className="login">
      <div className="login__logo-container">
        {/* Asegúrate de reemplazar 'path/to/logo.png' con la ruta real de tu logo */}
        <img src="path/to/logo.png" alt="Logo de Tecno Store" className="login__logo" />
      </div>
      <h1 className="login__title">Iniciar Sesión</h1>
      <InputField
        type="email"
        placeholder="correo electrónico"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <div className="login__input-container">
        <InputField
          type={showPassword ? 'text' : 'password'}
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className={showPassword ? 'login__input--show' : ''}
        />
        <i
          className="login__icon-eye"
          onClick={() => setShowPassword(!showPassword)}
        >
          👁️
        </i>
      </div>
      <LoginButton onClick={handleLogin}>Entrar</LoginButton>
      <p className="login__register">
        No tienes una cuenta? <a href="/register" className="login__register-link">Regístrate</a>
      </p>
    </div>
  );
};

export default Login;
