// InputField.jsx
import React from 'react';
import './InputField.css'; // Asegúrate de tener este archivo CSS para los estilos

const InputField = ({ type, placeholder, value, onChange, borderColor, icon }) => {
  return (
    <div className="input-field">
      <input
        type={type}
        className="input-field__input"
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        style={{ borderColor: borderColor }}
      />
      {icon && <i className="input-field__icon">{icon}</i>}
    </div>
  );
};

export default InputField;
