// Login.js
import React, { useState } from 'react';
import './Login.css';
import logo from './TECNO.png';
import eyeClosed from './eye_closed.png'; // La imagen del ojo cerrado
import eyeOpen from './eye_icon.png'; // La imagen del ojo abierto

const Login = () => {
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const togglePasswordVisibility = () => {
    setIsPasswordVisible(!isPasswordVisible);
  };

  return (
    <div className="login">
      <div className="login__logo-container">
        {/* Asegúrate de reemplazar 'path/to/logo.png' con la ruta real de tu logo */}
        <img src={logo} alt="Logo Tecno Store" className="login__logo" />
      </div>
      <h1 className="login__title" style={{ color: '#08DDB8' }}>Iniciar Sesión</h1>
      <div className="login__input-container">
        <input
          type="email"
          className="login__input"
          placeholder="Correo electrónico"
        />
      </div>
      <div className="login__input-container">
        <input
          type={isPasswordVisible ? 'text' : 'password'}
          className="login__input"
          placeholder="Contraseña"
        />
        <img
          src={isPasswordVisible ? eyeOpen : eyeClosed}
          alt="Mostrar/Ocultar contraseña"
          className="login__icon-eye"
          onClick={togglePasswordVisibility}
          style={{ opacity: isPasswordVisible ? '1' : '0.5' }} // La imagen se ve más clara cuando la contraseña está oculta
        />
      </div>
      <button className="login__button">Iniciar Sesión</button>
      <p className="login__register" style={{ color: '#08DDB8' }}>No tienes una cuenta? <a href="/register" className="login__register-link">Regístrate</a></p>
    </div>
  );
};

export default Login;
